<template>  <div class="time">    <div class="date">      <span>{{ dataTime }}</span      ><span>{{ time }}</span>    </div>  </div></template><script lang="ts">import { defineComponent, Ref, ref, onMounted, onBeforeMount } from 'vue'export default defineComponent({  name: 'date-time',  setup() {    let dataTime = ref('')    let time = ref('')    //定义 timer 初始值及类型    let timer: Ref<any> = ref(null)    // 当前时间    const getNowTime = () => {      const now = new Date()      const year = now.getFullYear()      const month = now.getMonth() >= 9 ? now.getMonth() + 1 : `0${now.getMonth() + 1}`      const date = now.getDate() >= 10 ? now.getDate() : `0${now.getDate()}`      const hour = now.getHours() >= 10 ? now.getHours() : `0${now.getHours()}`      const minutes = now.getMinutes() >= 10 ? now.getMinutes() : `0${now.getMinutes()}`      const seconds = now.getSeconds() >= 10 ? now.getSeconds() : `0${now.getSeconds()}`      dataTime.value = `${year}年${month}月${date}日`      time.value = `${hour}:${minutes}:${seconds}`    }    onMounted(() => {      getNowTime()      timer.value = setInterval(() => {        getNowTime()      }, 1000)    })    onBeforeMount(() => {      // 清理定时器要处理 timer 的类型      clearInterval(Number(timer))    })    return {      dataTime,      time    }  }})</script><style scoped lang="less">.time {  width: 100%;  .date {    height: 55px;    line-height: 55px;    text-align: center;    span {      color: @page-title-color;      font-family: @ds;      font-size: @module-highlight-size;      font-weight: bold;      letter-spacing: 2px;    }  }}</style>