<template>  <div class="cabin-pressure">    <xx-echart class="echart" :options="options" :width="width" :height="height" />  </div></template><script lang="ts">import { defineComponent, computed } from 'vue'import XxEchart from '@/base-ui/echart'export default defineComponent({  name: 'cabin-pressure',  components: {    XxEchart  },  props: {    pressureData: {      type: String    }  },  setup(props) {    const width = '400px'    const height = '300px'    const options = computed(() => {      return {        series: [          {            type: 'gauge',            center: ['50%', '60%'],            startAngle: 180,            endAngle: 0,            min: 0,            max: 100,            splitNumber: 10,            itemStyle: {              color: '#2AA9D8'            },            pointer: {              icon: 'path://M12.8,0.7l12,40.1H0.7L12.8,0.7z',              length: '25%',              width: 15,              offsetCenter: [0, '-40%'],              itemStyle: {                color: 'auto'              }            },            axisLine: {              lineStyle: {                width: 30,                color: [                  [0.3, '#60B583'],                  [0.7, '#ece814'],                  [1, '#E84240']                ]              }            },            axisTick: {              distance: -45,              splitNumber: 5,              lineStyle: {                width: 2,                color: '#999'              }            },            splitLine: {              distance: -52,              length: 14,              lineStyle: {                width: 3,                color: '#f3f3f3'              }            },            axisLabel: {              distance: -20,              color: '#f3f3f3',              fontSize: 20            },            anchor: {              show: false            },            title: {              offsetCenter: [0, '60%'],       